mapper.select:
SELECT * FROM app_info app
WHERE
1=1

	and app.id = #{appId}


	and app.dev_id = #{devId}


	and app.business_people_id = #{businessPeopleId}


	and app.app_type = #{appType}


	and app.audit_state = #{appState}


	and app.system_type = #{systemType}


	and app.join_up_type = #{joinUpType}

order by app.id desc
